mapper.update:
UPDATE lounge 
SET lounge_replycount = lounge_replycount + #{rpCntUpDown}
WHERE lounge_id = #{lounge_id};

mapper.select:
SELECT * FROM ( SELECT L.*,
(select count(B.like_id) from lounge_like B where B.lounge_id = L.lounge_id and B.user_id = #{user_id}) as likeState,
COUNT(LL.like_id) AS likeCnt FROM lounge L LEFT JOIN lounge_like LL ON L.lounge_id = LL.lounge_id WHERE L.lounge_date >= CURDATE() - INTERVAL 7 DAY GROUP BY L.lounge_id HAVING likeCnt >= 1 ORDER BY likeCnt DESC LIMIT 5 ) AS s ORDER BY likeCnt DESC;
